        "line-through": "none"
    }
/* long way to do it
    if(this.props.list.reserved) {
return {
    textDecoration: "line-through"
}
    } else {
        return {
            textDecoration: "none"
        }
    } */
}

    render() {
        return (
            <div style={this.getStyle()}>
               <p>
                   <input type="checkbox"/>
                   {this.props.list.place}
                   </p> 
            </div>
        )
    }
}

export default ListItem
